import React, { Component } from "react";
import Search from "./Search";
import Users from "./Users";
import Modal from "./Modal";
class HomeRedux extends Component {
  constructor(props) {
    super(props);
    this.state = {
      userList: [
        {
          id: 1,
          name: "Dinh Phuc Nguyen",
          username: "dpnguyen",
          email: "dpnguyen@gmail.com",
          phoneNumber: "1123123213",
          type: "VIP",
        },
        {
          id: 2,
          name: "Nguyen Dinh Phuc",
          username: "nguyendp",
          email: "nguyendp@gmail.com",
          phoneNumber: "1123123213",
          type: "VIP",
        },
      ],
      userEdit: null,
      keyword: ""
    };
  }

  timViTri = (id) => {
    return this.state.userList.findIndex((user) => {
      return id === user.id;
    });
  };

  handleDeleteUser = (id) => {
    const index = this.timViTri(id);
    let userList = [...this.state.userList];
    if (index !== -1) {
      userList.splice(index, 1);

      this.setState({
        userList,
      });
    }
  };

  handleOnSaveUser = (user) => {
    if (user.id) {
      // Update
      const index = this.timViTri(user.id);
      let userList = [...this.state.userList]
      if(index !== -1){
        userList[index] = user;
        this.setState({
          userList,
          userEdit: user
        })
      }
    } else {
      // Add
      const userList = [...this.state.userList];
      const id = Math.random().toString(36).substr(2, 5);
      userList.push({ ...user, id });

      this.setState({
        userList,
      });
    }

    // C2
    // this.setState(state => {
    //   return {
    //     userList: [...state.userList, {...user, id: Math.random() * 100}]
    //   }
    // })
  };

  handleGetUserEdit = (user) => {
    this.setState({
      userEdit: user
    });
  };

  handleGetKeyWord = (keyword) => {
    this.setState({
      keyword,
    })
  }

  render() {
    let {userList, keyword} = this.state;

    userList = userList.filter((item)=>{
      return (item.name.toLowerCase().indexOf(keyword.toLowerCase()) !== -1)
    });

    return (
      <div className="container">
        <h1 className="display-4 text-center my-3">User Management Redux</h1>
        <div className="d-flex justify-content-between align-items-center">
          <Search getKeyWord = {this.handleGetKeyWord}/>
          <button
            className="btn btn-success"
            data-toggle="modal"
            data-target="#modelIdUser"
            onClick={() => this.setState({ userEdit: null })}
          >
            Add User
          </button>
        </div>
        <Users
          userList={userList}
          deleteUser={this.handleDeleteUser}
          getUserEdit={this.handleGetUserEdit}
        />
        <Modal userEdit={this.state.userEdit} onSave={this.handleOnSaveUser} />
      </div>
    );
  }
}

export default HomeRedux;
